import React, { Component } from 'react';
import "material-components-web/dist/material-components-web.min.css";
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import Typography from '@material-ui/core/Typography';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import NavigationIcon from '@material-ui/icons/Navigation';
import Fab from '@material-ui/core/Fab';
import './Upcominglaunches.css'

const classes = theme => ({
    root: {
        flexGrow: 1,
    },
    card: {
    },
    media: {
        height: 352
    },
    margin: {
        margin: theme.spacing(1),
    },
    extendedIcon: {
        marginRight: theme.spacing(1),
    },
});

class UpcomingLaunches extends Component {

    constructor(props) {
        super(props);
        this.state = {
            UpcomingLaunches: [],
            isFetching: true
        }
    }

    componentDidMount() {
        fetch("https://api.spacexdata.com/v3/launches/upcoming")
            .then(res => res.json())
            .then((findresponse) => {
                try {
                    this.setState({
                        UpcomingLaunches: findresponse
                    })
                } catch (err) {
                    console.log(err);
                }
            })
            .catch((err) => {
                console.log({ status: 500, message: 'Internal Server Error', err });
            })
    }

    render() {
        const { classes } = this.props;
        const { isFetching } = this.state;
        let displaydata;
        console.log("upcoming launches", this.state.UpcomingLaunches);

        return (
            <div className={classes.root}>
                <h2>Upcoming Launches</h2>
                <div>
                    {
                        this.state.UpcomingLaunches.map(spacedata =>
                            <Grid key={spacedata} container spacing={3}>
                                <Grid item xs={6}>
                                    <Card className={classes.card}>
                                        <CardActionArea>
                                            <CardMedia
                                                className={classes.media}
                                                title="Contemplative Reptile"
                                            />
                                            <CardContent>
                                                <Typography gutterBottom variant="h5" component="h2">
                                                    {spacedata.rocket.rocket_name}<span>  - {spacedata.mission_name}</span>
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="fas fa-map-marker-alt"></i> {spacedata.launch_site.site_name_long}
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="far fa-calendar-alt"></i>
                                                    <strong className="date_color">{spacedata.launch_date_utc}</strong>
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="fas fa-user"></i>  {spacedata.rocket.second_stage.payloads[0].customers[0]}
                                                </Typography>
                                                {
                                                    spacedata.rocket.first_stage.cores[0].core_serial ?
                                                        (<div>
                                                            <Fab
                                                                variant="extended"
                                                                size="small"
                                                                color="primary"
                                                                aria-label="Add"
                                                                className={classes.margin}
                                                            >
                                                                <NavigationIcon className={classes.extendedIcon} />
                                                                {spacedata.rocket.first_stage.cores[0].core_serial}
                                                            </Fab>
                                                        </div>) : ('')
                                                }

                                            </CardContent>
                                        </CardActionArea>
                                    </Card>
                                </Grid>
                                <Grid item xs={6}>
                                    <Card className={classes.card}>
                                        <CardActionArea>
                                            <CardMedia
                                                className={classes.media}
                                                title="Contemplative Reptile"
                                            />
                                            <CardContent>
                                                <Typography gutterBottom variant="h5" component="h2">
                                                    {spacedata.rocket.rocket_name}<span>  - {spacedata.mission_name}</span>
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="fas fa-map-marker-alt"></i> {spacedata.launch_site.site_name_long}
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="far fa-calendar-alt"></i>
                                                    <strong className="date_color">{spacedata.launch_date_utc}</strong>
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="fas fa-user"></i>  {spacedata.rocket.second_stage.payloads[0].customers[0]}
                                                </Typography>
                                                {
                                                    spacedata.rocket.first_stage.cores[0].core_serial ?
                                                        (<div>
                                                            <Fab
                                                                variant="extended"
                                                                size="small"
                                                                color="primary"
                                                                aria-label="Add"
                                                                className={classes.margin}
                                                            >
                                                                <NavigationIcon className={classes.extendedIcon} />
                                                                {spacedata.rocket.first_stage.cores[0].core_serial}
                                                            </Fab>
                                                        </div>) : ('')
                                                }
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <Button className={classes.button}>REDDIT</Button>
                                                </Typography>
                                            </CardContent>
                                        </CardActionArea>
                                    </Card>
                                </Grid>
                            </Grid>
                        )}
                </div>
            </div>

        )
    }
}
UpcomingLaunches.propTypes = {
    container: PropTypes.object,
};

export default withStyles(classes)(UpcomingLaunches);
