import Fab from '@material-ui/core/Fab';
import './twitter.css';
import '../video.css';
import unregister from '../../intercept.js';
const Twitters = require('../../tweet.js').Twitters;
const fs = require('fs');
const OAuth = require('oauth').OAuth;

// const Twit = require('twit');
// const Twitters = require('twitter-node-client').Twitter;

// const twitter = new Twitters();
