import './twitter.css';
import '../video.css';
import unregister from '../../intercept.js';
import { TwitterTimelineEmbed, TwitterTweetEmbed } from 'react-twitter-embed';
const Twitters = require('../../tweet.js').Twitters;
const fs = require('fs');
const OAuth = require('oauth').OAuth;

