import React, { Component } from 'react';
import "material-components-web/dist/material-components-web.min.css";
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import Typography from '@material-ui/core/Typography';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Fab from '@material-ui/core/Fab';
import './Upcominglaunches.css';
import Tooltip from '@material-ui/core/Tooltip';
import API from '../service/upcominglaunches.service';

class AllLaunches extends Component {

    constructor(props) {
        super(props);
        this.state = {
            UpcomingLaunches: [],
            isFetching: true
        }
    }

    componentDidMount() {

        /**Upcoming Launches Spacex */
        API.getLaunches()
            .then((findresponse) => {
                try {
                    this.setState({
                        UpcomingLaunches: findresponse
                    })
                } catch (err) {
                    console.log(err);
                }
            })
            .catch((err) => {
                console.log({ status: 500, message: 'Internal Server Error', err });
            })
    }

    /** Render-app */
    render() {
        const { classes } = this.props;
        console.log("upcoming launches", this.state.UpcomingLaunches);

        return (
            <div className={classes.root}>
                <h2>Upcoming Launches</h2>
                <div>

                    {/** SpaceX-Upcoming Launches Response Display */}
                    {
                        this.state.UpcomingLaunches.map(spacedata =>
                            <Grid key={spacedata} container spacing={3}>
                                <Grid item xs={6}>

                                    {/** Content Display In Card */}
                                    <Card className={classes.card}>
                                        <CardActionArea>
                                            <CardMedia
                                                className={classes.media}
                                                title="Contemplative Reptile"
                                            />
                                            <CardContent>
                                                <Typography gutterBottom variant="h5" component="h2">
                                                    {spacedata.rocket.rocket_name}<span>  - {spacedata.mission_name}</span>
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="fas fa-map-marker-alt"></i> {spacedata.launch_site.site_name_long}
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="far fa-calendar-alt"></i>
                                                    <strong className="date_color">{spacedata.launch_date_utc}</strong>
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="fas fa-user"></i>  {spacedata.rocket.second_stage.payloads[0].customers[0]}
                                                </Typography>
                                                {
                                                    spacedata.rocket.first_stage.cores[0].core_serial ?
                                                        (<div>
                                                            <Tooltip title={spacedata.mission_name} aria-label="Add">
                                                                <Fab
                                                                    variant="extended"
                                                                    size="small"
                                                                    color="primary"
                                                                    aria-label="Add"
                                                                    className={classes.margin}
                                                                >
                                                                    🚀 {spacedata.rocket.first_stage.cores[0].core_serial}
                                                                </Fab>
                                                            </Tooltip>
                                                        </div>) : ('')
                                                }
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <Button className={classes.button}>REDDIT</Button>
                                                </Typography>
                                            </CardContent>
                                        </CardActionArea>
                                    </Card>
                                </Grid>
                                <Grid item xs={6}>

                                    {/** Content Display In Card */}
                                    <Card className={classes.card}>
                                        <CardActionArea>
                                            <CardMedia
                                                className={classes.media}
                                                title="Contemplative Reptile"
                                            />
                                            <CardContent>
                                                <Typography gutterBottom variant="h5" component="h2">
                                                    {spacedata.rocket.rocket_name}<span>  - {spacedata.mission_name}</span>
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="fas fa-map-marker-alt"></i> {spacedata.launch_site.site_name_long}
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="far fa-calendar-alt"></i>
                                                    <strong className="date_color">{spacedata.launch_date_utc}</strong>
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <i class="fas fa-user"></i>  {spacedata.rocket.second_stage.payloads[0].customers[0]}
                                                </Typography>
                                                {
                                                    spacedata.rocket.first_stage.cores[0].core_serial ?
                                                        (<div>
                                                            <Tooltip title={spacedata.mission_name} aria-label="Add">
                                                                <Fab
                                                                    variant="extended"
                                                                    size="small"
                                                                    color="primary"
                                                                    aria-label="Add"
                                                                    className={classes.margin}
                                                                >
                                                                    🚀 {spacedata.rocket.first_stage.cores[0].core_serial}
                                                                </Fab>
                                                            </Tooltip>
                                                        </div>) : ('')
                                                }
                                                <Typography variant="body2" color="textSecondary" component="p">
                                                    <Button className={classes.button}>REDDIT</Button>
                                                </Typography>
                                            </CardContent>
                                        </CardActionArea>
                                    </Card>
                                </Grid>
                            </Grid>
                        )}
                </div>
            </div>
        )
    }
}
AllLaunches.propTypes = {
    container: PropTypes.object,
};

export default withStyles(classes)(AllLaunches);
