import React, { Component } from 'react';
import "material-components-web/dist/material-components-web.min.css";
import Grid from '@material-ui/core/Grid';
import API from '../service/twitter.service.js';
import { Player } from 'video-react';
import './twitter.css';
import './video.css';

class Twitter extends Component {

    constructor(props) {
        super(props);
        this.state = {
            TwitterLaunches: [],
            TwitterMask:[]
        }
    }

    componentDidMount() {
        /** Twitter Launches spacex */
        API.getTwitterLaunches()
            .then((data) => {
                console.log("data===", data);
                try {
                    if (!data.length) {
                        // Swal.fire('Tweets Not Found....');
                        this.componentDidMount();
                    } else {
                        this.setState({
                            TwitterLaunches: data
                        })
                    }
                } catch (err) {
                    console.log(err);
                }
                console.log("twitter launches====", this.state.TwitterLaunches);
            })
            .catch((err) => {
                console.log({ status: 500, message: 'Internal Server Error', err });
            })

        /** Twitter Launches spacex */
        API.getTwittersLaunches()
            .then((data) => {
                console.log("data===", data);
                try {
                    if (!data.length) {
                        // Swal.fire('Tweets Not Found....');
                        this.componentDidMount();
                    } else {
                        this.setState({
                            TwitterMask: data
                        })
                    }
                } catch (err) {
                    console.log(err);
                }
                console.log("twitter launches====", this.state.TwitterMask);
            })
            .catch((err) => {
                console.log({ status: 500, message: 'Internal Server Error', err });
            })
    }

    /** Render-app */
    render() {
        return (
            <div>

                <h2>Tweets <span className="font_size">by</span>  <a className="mdc-list-item trends-color" target="_blank" href="https://twitter.com/SpaceX" aria-current="page">
                    <span className="font_size1">@SpaceX</span>
                </a></h2>
                <Grid container spacing={12}>
                    <Grid direction="row">
                        <Grid item xl={6} md={6} sm={6} >                        {
                            this.state.TwitterLaunches.map(tweet =>
                                <div key={tweet}>
                                    <div key={tweet}>
                                        <div className="tweet_class1">
                                            <Grid container spacing={1}>
                                                <Grid item sm={2}>
                                                    <div className="profile_image_post">
                                                        <img src={tweet.user.profile_image_url} />
                                                    </div>
                                                </Grid>
                                                <Grid item sm={10}>
                                                    <span className="username_title">{tweet.user.name}</span>
                                                                                         <a className="mdc-list-item trends-color" target="_blank" href={"http://twitter.com/" + tweet.user.screen_name} aria-current="page">
                                                        <span className="gray">@{tweet.user.screen_name}</span>
                                                    </a>
                                                    <p><span>{tweet.text}</span></p>
                                                    {(tweet.extended_entities) ?
                                                        (<div className="video">
                                                            {tweet.extended_entities ? (<Player className="video_height" src={tweet.extended_entities.media[0].video_info.variants[1].url}></Player>) : ('')}
                                                        </div>) : ('')}
                                                </Grid>
                                            </Grid>
                                        </div>
                                    </div>
                                </div>
                            )
                        }
                        </Grid>
                        <Grid item xl={6} md={6} sm={6}></Grid>
                    </Grid>
                </Grid>
            </div>
        )
    }
}

export default Twitter;
