import React, { Component } from 'react';
import "material-components-web/dist/material-components-web.min.css";
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import Typography from '@material-ui/core/Typography';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import PropTypes from 'prop-types';
import Swal from 'sweetalert2';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Fab from '@material-ui/core/Fab';
import './Upcominglaunches.css';
import Tooltip from '@material-ui/core/Tooltip';
import API from '../../service/upcominglaunches.service';
import unregister from '../../intercept.js';

const classes = theme => ({
    root: {
        flexGrow: 1,
    },
    card: {
    },
    media: {
        height: 352
    },
    margin: {
        margin: theme.spacing(1),
    },
    extendedIcon: {
        marginRight: theme.spacing(1),
    },
    button: {
        margin: theme.spacing(1),
    },
});

class UpcomingLaunches extends Component {

    select = e => document.querySelector(e);
    selectAll = e => document.querySelectorAll(e); 
    mainTl = new TimelineMax({repeat:-1, delay:1});

    /** constructor call */
    constructor(props) {
        super(props);
        this.state = {
            UpcomingLaunches: [],
            isFetching: true,
            isLoaded: false
        }
    }

    /** First this function call during component run */
    componentDidMount() {

        /**Upcoming Launches Spacex */
        API.getLaunches()
            .then((findresponse) => {
                this.setState({
                    UpcomingLaunches: findresponse,
                    isLoaded: true
                })
            })
            .catch((err) => {
                Swal.fire('Upcominglaunches Not Found....');
                console.log({ status: 500, message: 'Internal Server Error', err });
            })
    }

    /** Render-app */
    render() {
        const { classes } = this.props;
        const { isLoaded } = this.state;
        if (!isLoaded) {
            return (
                <center>
                    <div className="load">
                        <svg viewBox="0 0 600 600" xmlns="http://www.w3.org/2000/svg">
                            <defs>
                                <path id="rocketPath" d="M300,80c121.5,0,220,98.5,220,220S421.5,520,300,520,80,421.5,80,300,178.5,80,300,80" fill="none" stroke="#e7dece" stroke-miterlimit="10" />
                                <filter id="glow" x="-100%" y="-100%" width="250%" height="250%">
                                    <feGaussianBlur stdDeviation="15" result="coloredBlur" />
                                    <feOffset dx="0" dy="0" result="offsetblur"></feOffset>
                                    <feFlood id="glowAlpha" flood-color="#41C2F4" flood-opacity="0.71"></feFlood>
                                    <feComposite in2="offsetblur" operator="in"></feComposite>
                                    <feMerge>
                                        <feMergeNode />
